sql.where:
AND status = 'ACTIVE'

    AND expense_date >= #{dateFrom}


    AND expense_date <= #{dateTo}


    AND (
    expense_id LIKE CONCAT('%', #{keyword}, '%')
    OR warehouse_name LIKE CONCAT('%', #{keyword}, '%')
    OR category LIKE CONCAT('%', #{keyword}, '%')
    OR description LIKE CONCAT('%', #{keyword}, '%')
    )

mapper.select:
SELECT * FROM expense

ORDER BY expense_date DESC, expense_id DESC
LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE expense
SET status = 'DELETED'
WHERE expense_id = #{id}

mapper.select:
SELECT category, SUM(amount) AS totalAmount
FROM expense
WHERE YEAR(expense_date) = #{year}
  AND MONTH(expense_date) = #{month}
  AND status = 'ACTIVE'
GROUP BY category

mapper.update:
UPDATE expense
SET expense_code = #{expenseCode}
WHERE expense_id = #{id}

mapper.select:
SELECT COALESCE(SUM(amount), 0)
FROM expense
WHERE YEAR(expense_date) = #{year}
  AND MONTH(expense_date) = #{month}
  AND status = 'ACTIVE'
