mapper.select:
SELECT * FROM expense


        AND expense_date >= #{dateFrom}


        AND expense_date <= #{dateTo}


        AND (description LIKE CONCAT('%', #{keyword}, '%') OR category LIKE CONCAT('%', #{keyword}, '%'))


ORDER BY expense_date DESC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM expense


        AND expense_date >= #{dateFrom}


        AND expense_date <= #{dateTo}


        AND (description LIKE CONCAT('%', #{keyword}, '%') OR category LIKE CONCAT('%', #{keyword}, '%'))
