mapper.select:
SELECT * FROM inquiry WHERE status = 'AVAILABLE'
AND title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY created_at DESC LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE inquiry
SET title = #{title},
    content = #{content},
    updated_at = NOW()
WHERE inquiry_id = #{inquiryId}
AND password = #{password}

UPDATE inquiry
SET status = 'DELETED'
WHERE inquiry_id = #{inquiryId}
AND password = #{password}
