sql.where:
AND status = 'ACTIVE'

    AND sales_date >= #{dateFrom}


    AND sales_date <= #{dateTo}


    AND (
    sales_id LIKE CONCAT('%', #{keyword}, '%')
    OR sales_code LIKE CONCAT('%', #{keyword}, '%') OR warehouse_name LIKE CONCAT('%', #{keyword}, '%')
    OR client_name LIKE CONCAT('%', #{keyword}, '%')
    OR category LIKE CONCAT('%', #{keyword}, '%')
    OR description LIKE CONCAT('%', #{keyword}, '%')
    )

mapper.select:
SELECT * FROM sales

ORDER BY sales_date DESC, sales_id DESC
LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE sales
SET status = 'DELETED'
WHERE sales_id = #{id}

UPDATE sales
SET sales_code = #{salesCode}
WHERE sales_id = #{id}

mapper.select:
SELECT COALESCE(SUM(amount), 0)
FROM sales
WHERE YEAR(sales_date) = #{year}
  AND MONTH(sales_date) = #{month}
  AND status = 'ACTIVE'
